jQuery(function($){
	$('ul.tab-content').addClass('clearfix');
});


jQuery(document).ready(function($){
	$('.impact-button').click(function() {
		$(this).parents('.service-wrap').first().addClass('service-active');
		$(this).nearest('.service-more').addClass('service-more-active').slideDown(300);
		$('.service-wrap:not(.service-active)').hide(300);
		$(this).hide();
		$('html, body').animate({
        	scrollTop: $(".services-wrap").offset().top - 56
    	}, 700);
	});

	$('.impact-close, .x-close').click(function() {
		$(this).parent('.service-more-active').hide(300).removeClass('service-more-active');
		$(this).parents('.service-wrap').first().removeClass('service-active');
		$('.service-wrap, .impact-button').show(300);
		$('html, body').animate({
        	scrollTop: $(this).parents('.services-wrap').first().offset().top - 56
    	}, 700);
	});
});

jQuery(document).ready(function($){
	$('.project-more').click(function() {
		$(this).parents('.case-study').first().addClass('cs-active');
		$(this).nearest('.case-study-content').addClass('cs-more-active').slideDown(300);
		$('.case-study:not(.cs-active)').hide(300);
		$('html, body').animate({
        	scrollTop: $(this).parents('.case-studies').offset().top - 56
    	}, 700);
	});

	$('.cs-close, .project-close').click(function() {
		$(this).parents('.cs-more-active').hide(300).removeClass('cs-more-active');
		$(this).parents('.case-study').first().removeClass('cs-active');
		$('.case-study').show(300);
		$('html, body').animate({
        	scrollTop: $(this).parents('.case-studies').offset().top - 56
    	}, 700);
	});
});

jQuery(document).ready(function($){

  $('.bio-more').click(function() {
    $(this).parents('.bio-li').first().addClass('bio-active');
    $(this).nearest('.bio-content').addClass('bio-content-active').slideDown(300);
    $('.bio-li:not(.bio-active)').hide();
    $('html, body').animate({
          scrollTop: $(".bios-wrap").offset().top - 100
      }, 700);
    $('.experience-intro').hide();
  });
  
  $('.back-arrow').click(function() {
    $(this).nearest('.bio-content-active').hide(300).removeClass('bio-content-active');
    $(this).nearest('.bio-active').removeClass('bio-active');
    $('.bio-li').show(300);
    $('html, body').animate({
          scrollTop: $(".bios-wrap").offset().top - 100
      }, 700);
    $('.experience-intro').show();
});
});

jQuery(document).ready(function($){

  $('a[href*=#]:not([href=#])').click(function() {
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') 
        || location.hostname == this.hostname) {

        var target = $(this.hash);
        target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
           if (target.length) {
             $('html,body').animate({
                 scrollTop: target.offset().top - 56
            }, 700);
            return false;
        }
    }
});
  
});

